//

#include "stdafx.h"

char *cmds[5] = {
   "",
   "",
   "",
   "",
   ""
};

char keypad [7][7] ={
   {0x0,0x0,0x0,0x0,0x0,0x0,0x0},
   {0x0,0x0,0x0,0x1,0x0,0x0,0x0},
   {0x0,0x0,0x2,0x3,0x4,0x0,0x0},
   {0x0,0x5,0x6,0x7,0x8,0x9,0x0},
   {0x0,0x0,0xA,0xB,0xC,0x0,0x0},
   {0x0,0x0,0x0,0xD,0x0,0x0,0x0},
   {0x0,0x0,0x0,0x0,0x0,0x0,0x0}
};


int _tmain(int argc, _TCHAR* argv[])
{
   char code[5]={0};
   for(int i=0;i<5;i++)
   {
      char *cmd = cmds[i];
      int x=3,y=3;
      while(*cmd!=0)
      {
         int mx=0,my=0;
         switch(*cmd)
         {
            case 'U':
               my=-1;
            break;
            case 'D':
               my=1;
            break;
            case 'R':
               mx=1;
            break;
            case 'L':
               mx=-1;
            break;
         }
         if(keypad[y+my][x+mx]){y+=my;x+=mx;}         
         cmd++;
      }
      code[i] = keypad[y][x];
   }
   printf("Key Code %x%x%x%x%x",code[0],code[1],code[2],code[3],code[4]);
   getc(stdin);
	return 0;
}



